"use strict";

$((function() {
    $(window).scroll((function() {
        if ($(this).scrollTop() > 0) {
            $(".header").addClass("__scroll");
        } else {
            $(".header").removeClass("__scroll");
        }
    }));
    $(document).ready((function() {
        if ($(window).scrollTop() > 0) {
            $(".header").addClass("__scroll");
        } else {
            $(".header").removeClass("__scroll");
        }
    }));
}));

$(document).ready((function() {
    if ($(".js-mask-phone").length) {
        $(".js-mask-phone").mask("+7 (X99) 999-9999", {
            translation: {
                X: {
                    pattern: /9/,
                    optional: false
                }
            }
        });
    }
}));